mapper.update:
update product
set
pname = #{pname}, pprice=#{pprice}, pcount=#{pcount}, pimage = #{pimage}, plimit= now(), ptcode = #{ptcode}, pcontent = #{pcontent}
where pcode=#{pcode}

mapper.select:
select
@rownum:=@rownum+1 as no, product.*
from product
where (@rownum:=0)=0 and pdel = 0
order by pcode desc
limit #{start}, #{cntPerPage}
